  List<PLacesModel> places = [];
  void getPlaces() {
    FirebaseFirestore.instance.collection('places').get().then((value) {
      for (var element in value.docs) {
        places.add(PLacesModel.fromJson(element.data()));
      }
      emit(GetPlacesSuccess());
    }).catchError((error) {
      emit(GetPlacesError());
    });
  }

